import React, {FC, useState} from "react";
import GoodsList from '../components/GoodsList';
import goods from '../goods/goods.json'
import '../App.css'
import GoodsSorting from "../components/GoodsSorting";
import GoodsSortingOld from "../components/GoodsSortingOld";
import MySelect from "../components/UI/select/MySelect";
import FilterButton from "../components/UI/button/FilterButton";
import { title } from "process";

const Catalog: FC = () => {

    const [products, setProducts] = useState(goods)
    const [selectedSort, setSelectedSort] = useState('')
    let sortedGoods:any = getSortedGoods();

    function getSortedGoods() {
        if (!selectedSort) {
            return products
        } 
        let typeOfSort = (selectedSort.split('-'))
        if ((typeOfSort[0] == 'title') && (typeOfSort.length == 1)) {
            return  ([...products].sort((a, b) => a[selectedSort as keyof typeof a].toString().localeCompare(b[selectedSort as keyof typeof b].toString())))
        } else if ((typeOfSort[0] == 'title') && (typeOfSort.length == 2)) {
            return  ([...products].sort((a, b) => a[selectedSort as keyof typeof a].toString().localeCompare(b[selectedSort as keyof typeof b].toString()))).reverse()
        }
    }

    const sortGoods = (sort:string) => {
        setSelectedSort(sort);
    }




    const filterValues = ['body']
    const [selectedFilter, setSelectedFilter] = useState ('')
    const filerGoods = (filter:string) => {
        setSelectedFilter(filter)

        console.log(sortedGoods)
    }

    return (
        <div>
            <div className="header">HEADER OF CATALOG PAGE</div>
            <MySelect
                value={selectedSort}
                onChange={sortGoods}
                defaultValue='Сортировка'
                options={[
                    {value: 'title', name: 'По названию: Возрастание'},
                    {value: 'title-down', name: 'По названию: Убывание'},
                    {value: 'price', name: 'По цене: Возрастание'},
                    {value: 'price-down', name: 'По цене: Убывание'}
                ]}
            />
                <FilterButton
                    value={filterValues}
                    name = 'Уход за телом'
                    onClick={filerGoods} 
            />


            <GoodsList goods={sortedGoods} />
            <div className="footer">FOOTER</div>
      </div>
    );
};

export default Catalog;
