import React, {FC, useState} from "react";
import GoodsList from '../components/GoodsList';
import goods from '../goods/goods.json'
import '../App.css'
import MySelect from "../components/UI/select/MySelect";
import { IGoods } from "../types/types";

const Catalog: FC = () => {

    const [selectedSort, setSelectedSort] = useState('')

    const [products, setProducts] = useState(goods)

    const sortGoods = (sort:any) => {

        setSelectedSort(sort)
        let test = [...products][1];
        const temp = test[sort as keyof typeof test]

        //console.log(test[sort])
        console.log(temp)
        setProducts([...products].sort((a:string, b:IGoods) => a[sort as keyof typeof a].localeCompare(b[sort as keyof typeof b])))
      }


    return (
        <div>
            <div className="header">HEADER OF CATALOG PAGE</div>
            <MySelect
                value={selectedSort}
                onChange={sortGoods}
                defaultValue='Сортировка'
                options={[
                    {value: 'title', name: 'По названию'},
                    {value: 'price', name: 'По цене'}
                ]}
            />


            <GoodsList goods={goods} />
            <div className="footer">FOOTER</div>
      </div>
    );
};

export default Catalog;
