import React, {FC, useState} from "react";
import MyInput from "./UI/input/MyInput";

interface AddNewProductProps {
    create?: any;
}

const AddNewProduct: FC<AddNewProductProps> =({create}) => {

    const [product, setProduct] = useState ({title: ''})

    const createProduct = (e:any) => {
        e.preventDefault()
        const newProduct = {
            ...product, id: Date.now()
        }
        create(newProduct)
        setProduct({title: ''})
    }

    const [minPrice, setMinPrice] = useState('')
    const minPricing = (value:string) => {
        setMinPrice(value);
    }

    return (
        <div>
            <MyInput 
                value={minPrice}
                onChange={minPricing}
                placeholder = "Title"
            />
            <button onClick={createProduct}>Добавить позицию</button>
        </div>
    )
}

export default AddNewProduct
