import React, { useEffect, useMemo, useState } from 'react';
import goods from './goods/goods.json'
import './App.css'

import Catalog from './pages/Catalog';
import ProductCard from './pages/ProductCard';
import Basket from './pages/Basket';
import { BrowserRouter, Route, Routes } from 'react-router-dom';
import { NavLink } from 'react-router-dom';
import Header from './components/Header';
import { IGoods } from './types/types';

interface goods {
  goods: IGoods[];
}


function App() {

  const [productType, setProductType] = useState<undefined | string>('');
  //console.log(productType)
  const [cost, setCost] = useState<number> (0)

  const [orderList, setOrderList] = useState<any>([])

  useEffect(() => {
    if ((productType!== undefined) && (productType!=='') ) {
      if (!orderList.includes(goods[+productType - 1])) {
        setOrderList([...orderList, goods[+productType - 1]]);
        setCost(Math.ceil(cost + goods[+productType - 1].price))
      } else {
        console.log('ОН УЖЕ ДОБАВЛЕН')
      }


    }
  }, [productType]);  

  //console.log(orderList)
  const [orderThing, setOrderThing] = useState<undefined | Array<number>>();
  console.log(orderThing)
  const [sumOfMoney, setSumOfMoney] = useState <any>([])

  useEffect(() => { 
    if (orderThing!==undefined) {
      setSumOfMoney([...sumOfMoney, orderThing])
      sumOfMoney.findIndex( (item: any) => {
        item[0] === orderThing[0]
      } )
    }

  }, [orderThing]) 
  console.log(sumOfMoney)






  const [removeThing, setRemoveThing] = useState<undefined | IGoods>();
  useMemo(() => {
    let index = orderList.indexOf(removeThing, 0);
    if (index!== -1) {
      orderList.splice(index, 1);
      setOrderList(orderList)
    }
  }, [removeThing])

  return (
    <BrowserRouter>
      <div>
        <NavLink to="/">Каталог товаров</NavLink>
        <NavLink to="/basket">Корзина</NavLink>
        <Header 
            cost={cost}
            productTypeValue={productType}
            onProductTypeChange={setProductType}        
        />
      </div>
      <div>
        <Routes>
          <Route path='/' element={<Catalog
            goods={goods} 
            productTypeValue={productType}
            onProductTypeChange={setProductType}          
          />}></Route>
          <Route path='/catalog/:id' element={<ProductCard
            productTypeValue={productType}
            onProductTypeChange={setProductType}
          />}></Route>
          <Route path='/basket' element={<Basket
            orderList={orderList}
            onOrderThingChange={setOrderThing}
            onRemove={setRemoveThing} 
          />}></Route>
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
