import React, {FC, useEffect, useState} from "react";
import { IGoods } from "../types/types";
import CaretypeCheckbox from "./UI/checkbox/CaretypeCheckbox";
import MyInput from "./UI/input/MyInput";
import MySelect from "./UI/select/MySelect";

interface AdminItemProps {
    product: IGoods;
    onRemove? : (removeItem :IGoods) => void;
    onEdit? : (removeItem :any) => void;
};

const AdminItem: FC<AdminItemProps> = ({product, onRemove, onEdit}) => {
    function removing () {
        onRemove?.(product);
    }

    const [editValue, setEditValue] = useState (false)
    function editing () {
        setEditValue (!editValue)

        console.log(editValue)
    }

    useEffect (() => {
        onEdit?.([editValue, product]);
    }, [editValue])





    return (
        <div style={{display: 'flex', flexDirection: 'column'}}>
            <div style={{display: 'flex', margin: '10px 10px', padding: '0 10px', border: '2px solid black'}}>
                <div>
                    <p>ID#{product.id}. Название: {product.title}</p>
                    <p>Описание: {product.desription}</p>
                    <p>Прооизводитель: {product.producer}. Бренд: {product.brand}</p>
                    <p>Тип размера: {product.sizeType}. Размер: {product.size}</p>
                    <p>Штрихкод: {product.barcode}. Цена: {product.price} ₸</p>
                </div>
                <div style={{width: 200, borderLeft: ' 2px solid black', borderRight: ' 2px solid black', padding: '0 10px'}}>
                    <p>Тип ухода:</p>
                    {product.careType.map( type => 
                        <span key={type}>{type}, </span>)}
                </div>
                <div style={{borderRight: ' 2px solid black'}}>
                    <img src={product.image} alt={product.image} height={150}/>
                </div>
                <div>
                    <button onClick={removing}>Удалить</button>
                    <button onClick={editing}>Редактировать</button>
                </div>
            </div>
            {editValue && <div>НАЧИНАЕМ РЕДАКТИРОВАТЬ
                <div style={{display: 'flex', flexDirection: 'column', maxWidth: '300px'}}>
                <MyInput value={product.title} onChange={titleChanger} placeholder = "Title" />
                <MyInput value={product.image} onChange={imgURLChanger} placeholder = "ImageURL" />
                <MySelect
                    value={product.sizeType}
                    onChange={sizeTypeChanger}
                    defaultValue='Тип размера'
                    options={[
                        {value: 'weight', name: 'Вес (гр)'},
                        {value: 'volume', name: 'Объем (мл)'}
                    ]}
                />
            <MyInput value={product.size} type={'number'} onChange={sizeChanger} placeholder = 'Размер' />
            <MyInput value={product.barcode} type={'number'} onChange={barcodeChanger} placeholder = 'Штрихкод' />
            <MyInput value={product.producer} onChange={producerChanger} placeholder = 'Производитель' />
            <MyInput value={product.brand} onChange={brandChanger} placeholder = 'Бренд' />
            <MyInput value={product.desription} onChange={desriptionChanger} placeholder = 'Описание' />
            <MyInput value={price} type={'number'} onChange={priceChanger} placeholder = 'Цена' />
            <div>Укажите тип товара:
                <div>
                    <CaretypeCheckbox name='Уход за телом' value='body' onClick={careTypeChanger} /> 
                    <CaretypeCheckbox name='Уход за руками' value='hands' onClick={careTypeChanger} />
                    <CaretypeCheckbox name='Уход за ногами' value='legs' onClick={careTypeChanger} />  
                    <CaretypeCheckbox name='Уход за лицом' value='face' onClick={careTypeChanger} />
                    <CaretypeCheckbox name='Уход за волосами' value='hair' onClick={careTypeChanger} /> 
                    <CaretypeCheckbox name='Средства для загара' value='sun' onClick={careTypeChanger} />
                    <CaretypeCheckbox name='Средства для бритья' value='shaving' onClick={careTypeChanger} />  
                    <CaretypeCheckbox name='Подарочные наборы' value='present' onClick={careTypeChanger} />
                    <CaretypeCheckbox name='Гигиеническая продукция' value='hygienic' onClick={careTypeChanger} />
                    <CaretypeCheckbox name='Гигиена полости рта' value='mouth' onClick={careTypeChanger} /> 
                    <CaretypeCheckbox name='Бумажная продукция' value='paper' onClick={careTypeChanger} />                  
                </div>

            </div>
            <button onClick={createProduct}>Добавить позицию</button>
        </div>           



            </div>}

        </div>
    )    
}

export default AdminItem
