import React, {FC, useEffect, useMemo, useState} from "react";
import '../App.css'
import BasketItem from "../components/BasketItem";
import GoodsItem from "../components/GoodsItem";
import Header from "../components/Header";
import OrdersList from "../components/OrdersList";
import { IGoods } from "../types/types";

interface BasketProps{
    orderList: IGoods[];
    onOrderThingChange?: (OrderThing: Array<number>) => void;
    onRemove? : (removeItem :IGoods) => void;
    finalPrice: number;
    basketThingsArray?:any;
}

const Basket: FC<BasketProps> = ({orderList, onOrderThingChange, onRemove, finalPrice, basketThingsArray}) => {


    const [total, setTotal] = useState<undefined | Array<number>>();
    // console.log(total)
    useEffect( () => {
        if (total) {
        onOrderThingChange?.(total)
        } 
    }, [total])


    let newBasketArray = []
    for (let i=0; i<basketThingsArray.length; i++) {
    newBasketArray.push( {id: basketThingsArray[i][0], basketCount: basketThingsArray[i][1]})
    }

    console.log(newBasketArray)
    console.log(orderList)


    const [removeThing, setRemoveThing] = useState<undefined | IGoods>();
    //console.log(removeThing)
    useEffect( () => {
        if (removeThing) {
            onRemove?.(removeThing)
        }
    }, [removeThing])

    function merge() {
        var hash: Object = {}; // временный хэш объектов по свойству id
        for (var l = 0; l < arguments.length; l++) {
            var arr = arguments[l];
            if (!arr.length) continue;
            for (var i = 0; i < arr.length; i++) {
                var el = arr[i];
                if (!('id' in el)) continue;
                var id = el.id;
                if (!hash[id]) hash[id] = {};
                for (var key in el) {
                    if (el.hasOwnProperty(key))
                        hash[id][key] = el[key];
                }
            }
        }
        var result = [];
        for (var id in hash) {
            if (hash.hasOwnProperty(id)) result.push(hash[id]);
        }
        return result;
    }





    return (
        <div>
            <div>КОРЗИНА</div>
            <div className="goods-list__list">
            {orderList.map( order => 
                <div key={order.id} >
                    <BasketItem 
                        order={order}
                        onTotalChange={setTotal} 
                        onRemove={setRemoveThing}
                    />
                </div>    
            )}
            <button>Оформить заказ</button>
            <p>Итого: {finalPrice} ₸</p>
        </div>
            <div className="footer">FOOTER</div>
        </div>
    );
};

export default Basket;
