import React, {FC, useState} from "react";
import GoodsList from '../components/GoodsList';
import goods from '../goods/goods.json'
import '../App.css'
import GoodsSorting from "../components/GoodsSorting";
import GoodsSortingOld from "../components/GoodsSortingOld";
import MySelect from "../components/UI/select/MySelect";
import FilterButton from "../components/UI/button/FilterButton";

const Catalog: FC = () => {

    const [products, setProducts] = useState(goods)
    const [selectedSort, setSelectedSort] = useState('')
    let sortedPost = [];

    const sortGoods = (sort:string) => {
        setSelectedSort(sort);

        let element = [...products][0];
        let sortDirection = sort.split('-')[1];
        sort = sort.split('-')[0];

        if ((typeof element[sort as keyof typeof element] == 'string') && (sortDirection == 'down')) {
            sortedPost = ([...products].sort((a, b) => a[sort as keyof typeof a].toString().localeCompare(b[sort as keyof typeof b].toString()))).reverse()
        }else if (typeof element[sort as keyof typeof element] == 'string') {
            sortedPost = ([...products].sort((a, b) => a[sort as keyof typeof a].toString().localeCompare(b[sort as keyof typeof b].toString())))
        }else if ((typeof element[sort as keyof typeof element] == 'number') && (sortDirection == 'down')){
            sortedPost = [...products].sort((a, b) =>  +b[sort as keyof typeof b] - +a[sort as keyof typeof a])  
        }else {
            sortedPost = [...products].sort((a, b) => +a[sort as keyof typeof a] - +b[sort as keyof typeof b])
        }
    }




    const filterValues = ['body']
    const [selectedFilter, setSelectedFilter] = useState ('')
    const filerGoods = (filter:string) => {
        setSelectedFilter(filter)

        console.log(filter)
    }

    return (
        <div>
            <div className="header">HEADER OF CATALOG PAGE</div>
            <MySelect
                value={selectedSort}
                onChange={sortGoods}
                defaultValue='Сортировка'
                options={[
                    {value: 'title-up', name: 'По названию: Возрастание'},
                    {value: 'title-down', name: 'По названию: Убывание'},
                    {value: 'price-up', name: 'По цене: Возрастание'},
                    {value: 'price-down', name: 'По цене: Убывание'}
                ]}
            />
                <FilterButton
                    value={filterValues}
                    name = 'Уход за телом'
                    onClick={filerGoods} 
            />


            <GoodsList goods={products} />
            <div className="footer">FOOTER</div>
      </div>
    );
};

export default Catalog;
