import React, {FC, useState} from "react";
import MyInput from "./UI/input/MyInput";
import MySelect from "./UI/select/MySelect";

interface AddNewProductProps {
    create?: any;
}

const AddNewProduct: FC<AddNewProductProps> =({create}) => {

    const [product, setProduct] = useState ({title: '', image: '', sizeType: '', size: '', barcode: '', producer: '', brand: '', desription: ''})
    const createProduct = (e:any) => {
        e.preventDefault()
        const newProduct = {
            ...product, id: Date.now()
        }
        create(newProduct)
        setProduct({title: title, image: imgURL, sizeType: sizeType, size: size, barcode: barcode, producer: producer, brand: brand, desription: desription})
        console.log(product)
    }

    const [title, setTitle] = useState('')
    const titleChanger = (value:string) => {
        setTitle(value);
    }

    const [imgURL, setimgURL] = useState('')
    const imgURLChanger = (value:string) => {
        setimgURL(value);
    }

    const [sizeType, setsizeType] = useState('')
    const sizeTypeChanger = (value:string) => {
        setsizeType(value);
        console.log(sizeType)
    }

    const [size, setSize] = useState('')
    const sizeChanger = (value:string) => {
        setSize(value);
    }

    const [barcode, setBarcode] = useState('')
    const barcodeChanger = (value:string) => {
        setBarcode(value);
    }

    const [producer, setProducer] = useState('')
    const producerChanger = (value:string) => {
        setProducer(value);
    }

    const [brand, setBrand] = useState('')
    const brandChanger = (value:string) => {
        setBrand(value);
    }

    const [desription, setDesription] = useState('')
    const desriptionChanger = (value:string) => {
        setDesription(value);
    }

    const [price, setPrice] = useState('')
    const priceChanger = (value:string) => {
        setPrice(value);
    }

    return (
        <div style={{display: 'flex', flexDirection: 'column', maxWidth: '300px'}}>
            <MyInput value={title} onChange={titleChanger} placeholder = "Title" />
            <MyInput value={imgURL} onChange={imgURLChanger} placeholder = "ImageURL" />
            <MySelect
                        value={sizeType}
                        onChange={sizeTypeChanger}
                        defaultValue='Тип размера'
                        options={[
                            {value: 'weight', name: 'Вес (гр)'},
                            {value: 'volume', name: 'Объем (мл)'}
                        ]}
            />
            <MyInput value={size} onChange={sizeChanger} placeholder = 'Размер' />
            <MyInput value={barcode} onChange={barcodeChanger} placeholder = 'Штрихкод' />
            <MyInput value={producer} onChange={producerChanger} placeholder = 'Производитель' />
            <MyInput value={brand} onChange={brandChanger} placeholder = 'Бренд' />
            <MyInput value={desription} onChange={desriptionChanger} placeholder = 'Описание' />
            <MyInput value={price} onChange={priceChanger} placeholder = 'Цена' />
            <button onClick={createProduct}>Добавить позицию</button>
        </div>
    )
}

export default AddNewProduct
