import React, {FC, useState} from "react";
import MyInput from "./UI/input/MyInput";
import MySelect from "./UI/select/MySelect";

interface AddNewProductProps {
    create?: any;
}

const AddNewProduct: FC<AddNewProductProps> =({create}) => {

    const [product, setProduct] = useState ({title: '', image: ''})
    const createProduct = (e:any) => {
        e.preventDefault()
        const newProduct = {
            ...product, id: Date.now()
        }
        create(newProduct)
        setProduct({title: title, image: imgURL})
        console.log(product)
    }

    const [title, setTitle] = useState('')
    const titleChanger = (value:string) => {
        setTitle(value);
    }

    const [imgURL, setimgURL] = useState('')
    const imgURLChanger = (value:string) => {
        setimgURL(value);
    }

    const [sizeType, setsizeType] = useState('')
    const sizeTypeChanger = (value:string) => {
        setsizeType(value);
    }


    return (
        <div>
            <MyInput value={title} onChange={titleChanger} placeholder = "Title" />
            <MyInput value={imgURL} onChange={imgURLChanger} placeholder = "ImageURL" />
            <MySelect
                        value={sizeType}
                        onChange={sizeTypeChanger}
                        defaultValue='Название'
                        options={[
                            {value: 'price', name: 'volume'},
                            {value: 'price-down', name: 'Цена: По убыванию'},
                            {value: 'title', name: 'Название: По возрастанию'},
                            {value: 'title-down', name: 'Название: По убыванию'}
                        ]}
            />
            <button onClick={createProduct}>Добавить позицию</button>
        </div>
    )
}

export default AddNewProduct
