import React, {FC, useState} from "react";
import goods from '../goods/goods.json'
import '../App.css'
import GoodsItem from "../components/GoodsItem";
import { useNavigate, useParams } from "react-router-dom";
import Header from "../components/Header";


type ProductCardParams = {
    id: string;
}

const ProductCard: FC = () => {
    const params = useParams<ProductCardParams>();
    const navigate = useNavigate();



    function sentToBasket (productFromBasket:any) {
const [productToBasket, setProductProductToBasket] = useState(0)

        //Получаем данные
        console.log(productFromBasket)
        //Передаем данные в хедер
        setProductProductToBasket(1)  
    }

    return (
        <div>
            <Header product={productToBasket}/>
            <button onClick={sentToBasket}>Передать 1 в корзину</button>
            <button onClick={() => {navigate('/')}}>Назад</button>
            <GoodsItem 
                good={goods[Number(params.id)]}
                //toBasket ={sentToBasket}
            />
            <div className="footer">FOOTER</div>
        </div>
    );
};

export default ProductCard;
