import { IGoods } from "../../../types/types";

interface MyCheckboxProps {
    key: string;
    producer: any;
    onClick?: any;

}



const MyCheckbox:FC<MyCheckboxProps> = ({producer, onClick}) => {

    let producerName = producer[0];
    let deleteProducer = producer[0];
    const [checked, setChecked] = useState(true);

    function clickHandler (producerName:any, event:any) {
        if (checked==true) {
           onClick(producerName) 
           setChecked(!checked)
        } else {
            onClick(producerName) 
            setChecked(!checked)
        }


        console.log(checked)
    }

    return (
        <div>
            <input 
                type="checkbox" 
                name={producerName}
                onClick={ (event) => clickHandler({producerName}, event)
                    }

            />
            <label htmlFor={producerName}>{producerName} ({producer[1]})</label>
        </div>

    );
};

export default MyCheckbox
