import React, {FC, useState} from "react";
import GoodsList from '../components/GoodsList';
import goods from '../goods/goods.json'
import '../App.css'
import MySelect from "../components/UI/select/MySelect";
import { IGoods } from "../types/types";

const Catalog: FC = () => {

    const [selectedSort, setSelectedSort] = useState('')

    const [products, setProducts] = useState(goods)

    const sortGoods = (sort:string) => {
        setSelectedSort(sort)
        console.log(sort.split('-')[1])




        if ((sort.split('-')[1]) == 'up') {
            sort = sort.split('-')[0];
            setProducts([...products].sort((a:IGoods, b:IGoods) => a[sort as keyof typeof a].toString().localeCompare(b[sort as keyof typeof b].toString())))
        } else {
            sort = sort.split('-')[0];
            setProducts(([...products].sort((a:IGoods, b:IGoods) => a[sort as keyof typeof a].toString().localeCompare(b[sort as keyof typeof b].toString()))).reverse())
        }
        //setProducts([...products].sort((a:IGoods, b:IGoods) => a[sort as keyof typeof a].toString().localeCompare(b[sort as keyof typeof b].toString())))
      }


    return (
        <div>
            <div className="header">HEADER OF CATALOG PAGE</div>
            <MySelect
                value={selectedSort}
                onChange={sortGoods}
                defaultValue='Сортировка'
                options={[
                    {value: 'title-up', name: 'По названию: Возрастание'},
                    {value: 'title-down', name: 'По названию: Убывание'},
                    {value: 'price-up', name: 'По цене: Возрастание'},
                    {value: 'price-down', name: 'По цене: Убывание'}
                ]}
            />


            <GoodsList goods={products} />
            <div className="footer">FOOTER</div>
      </div>
    );
};

export default Catalog;
