import React, {FC, useState} from "react";
import { IGoods } from "../../../types/types";

interface MyCheckboxProps {
    key: string;
    producer: any;
    onClick?: any;

};
let clasAdding = 0;

const MyCheckbox:FC<MyCheckboxProps> = ({producer, onClick}) => {



    let producerName = producer[0];
    const [checked, setChecked] = useState(true);

    function clickHandler () {
        if (checked==true) {
           onClick(['add', producerName]) 
           setChecked(!checked)
        } else {
            onClick(['remove', producerName]) 
            setChecked(!checked)
        }
    };

    return (
        <div>
            <input 
                type="checkbox" 
                name={producerName}
                onClick={ (event) => clickHandler()}

            />
            <label htmlFor={producerName}>{producerName} ({producer[1]})</label>
        </div>
    );
};

export default MyCheckbox;
