import React, {FC, useState} from "react";
import goods from '../goods/goods.json'
import '../App.css'
import GoodsItem from "../components/GoodsItem";
import { useNavigate, useParams } from "react-router-dom";
import Header from "../components/Header";


type ProductCardParams = {
    id: string;
}

interface ProductCardProps {
    productTypeValue?: string;
    onProductTypeChange?: (newType: string) => void;
}

const ProductCard: FC = ({productTypeValue, onProductTypeChange}) => {
    const params = useParams<ProductCardParams>();
    const navigate = useNavigate();

    const [productType, setProductType] = useState<undefined | string>('');
    console.log('в App получили ID = ' + productType)

    return (
        <div>
            <Header productTypeValue={productType}/>
            {/* <button onClick={sentToBasket}>Передать в хедер</button> */}
            <button onClick={() => {navigate('/')}}>Назад</button>
            <GoodsItem 
                good={goods[Number(params.id)]}
                productTypeValue={productType}
                onProductTypeChange={setProductType}
            />
            <div className="footer">FOOTER</div>
        </div>
    );
};

export default ProductCard;
