import React, {FC, useState} from "react";
import MyInput from "../components/UI/input/MyInput";

const AdminPage: FC =() => {

    const [products, setProducts] = useState([
        {}
    ])
console.log(products)

    const [product, setProduct] = useState ({title: ''})

    const createProduct = (e:any) => {
        e.preventDefault()
        // const newProduct = {
        //     ...product, title
        // }
        setProducts([...products, {...product, id: Date.now()}])
        setProduct({ title: '' })


    }


    const [title, setTitle] = useState('')
    const titleValue = (value:string) => {
        setTitle(value);
        setProduct({...product, title})
        console.log(title)
    }


    return (
        <div>
            <MyInput 
                value={title}
                onChange={titleValue}
                placeholder = "0"
            />
            <button onClick={createProduct}>Добавить позицию</button>
        </div>
    )
}

export default AdminPage;
