import React, {FC, useMemo, useState} from "react";
import { IGoods } from "../types/types";
import MyCheckbox from "./UI/checkbox/MyCheckbox";

interface CheckboxListProps {
    value: any;
    filtredGoodsList: IGoods[];
    onClick?: any;
}

let boxesArray:any = []

const CheckboxList:FC<CheckboxListProps> = ({value, filtredGoodsList, onClick}) => {

    let producersArray:any[] = filtredGoodsList.map( good => good.producer);
    producersArray = producersArray.reduce((acc, i) => {
        if (acc.hasOwnProperty(i)) {
          acc[i] += 1;
        } else {
          acc[i as keyof typeof acc] = 1;
        }
        return acc;
    }, []);
    let recycledproducersArray = [];
    for (var key in producersArray) {
        recycledproducersArray.push([key, producersArray[key]]);
    };


    const [clickChecker, setclickChecker] = useState(false)
    function clickHandler (e:any) {
        e.stopPropagation();
        setclickChecker(!clickChecker)
        console.log(clickChecker)
    }

    return (
        <div>
            {recycledproducersArray.map((producer) =>          
                <MyCheckbox 
                    key={producer[0]} 
                    producer={producer}
                    onClick = {onClick}
                />)
            }
            <button onClick={(e) => clickHandler}>Показать все</button>
        </div>
    );
};

export default CheckboxList
