import React, {FC} from "react";
import { IGoods } from "../types/types";
import '../App.css'


interface GoodsItemProps {
    good: IGoods
};

const GoodsItem: FC<GoodsItemProps> = ({good}) => {

    let weigthType: string = good.sizeType;
    if (weigthType == 'weight') {
        weigthType = 'г'
    } else {
        weigthType = 'мл'
    };

    return (
        <div className="GoodsList__GoodsItem">
            <img src={good.image} alt="Img" height='200px' />
            {good.sizeType == 'weight' ? (
                <div>
                    {good.size} г
                </div>
            ) : (
                <div>
                    {good.size} мл
                </div>
            )}

            <p>{good.title} {good.desription}</p>
            <p>Штрихкод: {good.barcode}</p>
            <p>Производитель: {good.producer}</p>
            <p>Бренд: {good.brand}</p>
            <div>
            <p>{good.price} ₸</p>
            <button>В корзину</button>
            </div>
      </div>
    )
}

export default GoodsItem;
