import React, {FC, useState} from "react";
import goods from '../goods/goods.json'
import '../App.css'
import GoodsItem from "../components/GoodsItem";
import { useNavigate, useParams } from "react-router-dom";
import Header from "../components/Header";


type ProductCardParams = {
    id: string;
}

const ProductCard: FC = (product) => {
    const params = useParams<ProductCardParams>();
    const navigate = useNavigate();

    const [productToBasket, setProductProductToBasket] = useState(0)
    function sentToBasket () {
        //Передаем данные в хедер
        setProductProductToBasket(1)  
    }

    const [productFromBasket, setProductFromToBasket] = useState(10)

    function fromBasket() {
        console.log(productFromBasket)
    }


    return (
        <div>
            <Header product={productToBasket}/>
            <button onClick={sentToBasket}>Передать в хедер</button>
            <button onClick={fromBasket}>Получить из карточки</button>
            <button onClick={() => {navigate('/')}}>Назад</button>
            <GoodsItem 
                good={goods[Number(params.id)]}
                toBasket ={setProductFromToBasket}
            />
            <div className="footer">FOOTER</div>
        </div>
    );
};

export default ProductCard;
